mapper.select:
SELECT id, pwd, profile, email, regdate
FROM users
WHERE id=#{id}

mapper.update:
UPDATE users
SET pwd=#{newPwd}
WHERE id=#{id}

UPDATE users
SET email=#{email }, profile=#{profile, jdbcType=VARCHAR}
WHERE id=#{id }

UPDATE users
SET email=#{email } , profile=#{profile }
WHERE id=#{id }
